CREATE TABLE `new_employee_documents` ( `document_id` INT NOT NULL AUTO_INCREMENT, `employee_id` INT NULL, `card_type` VARCHAR(45) NULL, `card_no` VARCHAR(45) NULL, `card_name` VARCHAR(45) NULL, `file` LONGTEXT NOT NULL, `expiry_date` DATE NULL, `is_verified` TINYINT NULL, `created_at` TIMESTAMP NULL DEFAULT CURRENT_TIMESTAMP, `updated_at` TIMESTAMP NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP, PRIMARY KEY (`document_id`));