CREATE TABLE `outlets` ( `outlet_id` int(11) NOT NULL, `outlet_name` varchar(255) DEFAULT NULL, `outlet_address` varchar(200) DEFAULT NULL, `outlet_phone` varchar(100) DEFAULT NULL, `phone` text, `outlet_nickname` varchar(100) DEFAULT NULL, `is_active` tinyint(4) NOT NULL DEFAULT '1', `created_at` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP, `updated_at` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP, PRIMARY KEY (`outlet_id`)) ENGINE=InnoDB DEFAULT CHARSET=utf8;