CREATE TABLE `company_details` ( `company_id` INT NOT NULL AUTO_INCREMENT, `company_name` VARCHAR(45) NOT NULL, `reg_address` LONGTEXT NOT NULL, `contact_number` VARCHAR(45) NOT NULL, `gst_number` VARCHAR(45) NOT NULL, `pan_number` VARCHAR(45) NOT NULL, `esi_number` VARCHAR(45) NOT NULL, `tan_number` VARCHAR(45) NOT NULL, `pf_number` VARCHAR(45) NOT NULL, `created_at` TIMESTAMP NULL DEFAULT CURRENT_TIMESTAMP, `status` TINYINT NOT NULL DEFAULT '1', PRIMARY KEY (`company_id`));