CREATE TABLE `store` ( `store_id` int NOT NULL AUTO_INCREMENT, `store_name` varchar(255) DEFAULT NULL, `short_name` varchar(5) NOT NULL, `email_id` varchar(150) DEFAULT NULL, `contact_no` varchar(50) DEFAULT NULL, `contact_person_name` varchar(100) DEFAULT NULL, `contact_person_phone` varchar(50) DEFAULT NULL, `address` text, `status` int DEFAULT '1', `lock_status` int NOT NULL, `lock_date` date NOT NULL, `ob1` varchar(150) NOT NULL, `ob2` varchar(150) NOT NULL, `online_portal` int NOT NULL, PRIMARY KEY (`store_id`));