CREATE TABLE `payment_det` ( `payment_id` INT NOT NULL AUTO_INCREMENT, `employee` VARCHAR(45) NULL, `loan_amount` INT NULL, `installment_duration` VARCHAR(45) NULL, `paid_status` int NOT NULL DEFAULT '0', `status` int NOT NULL DEFAULT '0', `created_at` TIMESTAMP NULL DEFAULT CURRENT_TIMESTAMP, `updated_at` TIMESTAMP NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP, PRIMARY KEY (`payment_id`));